package programs;

public class swapping {

	public static void main(String[] args) {
		int a=10;
		int b=30;
		System.out.println("Before swapping a is"+a+ "b is" +b);
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println("After swapping a is"+a+ "b is"+b);
		// TODO Auto-generated method stub

	}

}
